package com.skillstorm.models;

import java.util.Objects;

import javax.persistence.Column;
import javax.persistence.Entity;
import javax.persistence.GeneratedValue;
import javax.persistence.GenerationType;
import javax.persistence.Id;
import javax.persistence.Table;

// Entity tells our app that this class refers to a database record
@Entity
// Table tells it which specific table it refers to
@Table(name = "lead_actress")
public class LeadActress {
	
	// Id indicates that this property is a primary key
	@Id
	// GeneratedValue indicates that this field will be auto-generated by the database
	@GeneratedValue(strategy = GenerationType.IDENTITY)
	// Column tells us which table column goes with this variable
	@Column(name = "lead_actress_id")
	private int leadActressId;
	
	@Column(name = "name")
	private String name;
	
	@Column(name = "age")
	private int age;
	
	@Column(name = "academy_awards")
	private int academyAwards;
	
	@Column(name = "image_url")
	private String imageUrl;
	
	@Column(name = "imdb_url")
	private String imdbUrl;
	
	public LeadActress() {}
	
	public LeadActress(int leadActressId, String name, int age, int academyAwards, String imageUrl, String imdbUrl) {
		super();
		this.leadActressId = leadActressId;
		this.name = name;
		this.age = age;
		this.academyAwards = academyAwards;
		this.imageUrl = imageUrl;
		this.imdbUrl = imdbUrl;
	}

	public int getLeadActressId() {
		return leadActressId;
	}

	public void setLeadActressId(int leadActressId) {
		this.leadActressId = leadActressId;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public int getAge() {
		return age;
	}

	public void setAge(int age) {
		this.age = age;
	}

	public int getAcademyAwards() {
		return academyAwards;
	}

	public void setAcademyAwards(int academyAwards) {
		this.academyAwards = academyAwards;
	}

	public String getImageUrl() {
		return imageUrl;
	}

	public void setImageUrl(String imageUrl) {
		this.imageUrl = imageUrl;
	}

	public String getImdbUrl() {
		return imdbUrl;
	}

	public void setImdbUrl(String imdbUrl) {
		this.imdbUrl = imdbUrl;
	}

	@Override
	public String toString() {
		return "LeadActress [leadActressId=" + leadActressId + ", name=" + name + ", age=" + age + ", academyAwards="
				+ academyAwards + ", imageUrl=" + imageUrl + ", imdbUrl=" + imdbUrl + "]";
	}

	@Override
	public int hashCode() {
		return Objects.hash(academyAwards, age, imageUrl, imdbUrl, leadActressId, name);
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		LeadActress other = (LeadActress) obj;
		return academyAwards == other.academyAwards && age == other.age && Objects.equals(imageUrl, other.imageUrl)
				&& Objects.equals(imdbUrl, other.imdbUrl) && leadActressId == other.leadActressId
				&& Objects.equals(name, other.name);
	}
	
}
